function program($n)
{   
    $numberofchars=strlen($n);
    $floor = 0;

    for ($i = 0; $i < $numberofchars; $i++) {
        if ($n[$i] === '(') {
            $floor++;
        } else {
            $floor--;
        }
        if($floor=== -1){
            echo ($i+1).', ';
        }
    }
    echo '<br/>';
    echo $floor;
}

