function program($n)
{   
    $numberofchars=strlen($n);
    $floor = 0;

    for ($i = 0; $i < $numberofchars; $i++) {
        if ($n[$i] === '(') {
            $floor++;
        } else {
            $floor--;
        }
    }

    echo $floor;
}

